            var form=checkbox.form;
            if (!(form)) form=getParent(checkbox,".fdjtinputs");
            if (!(form)) form=checkspan.parentNode;
            var name=checkbox.name;
            var tosync=getChildren(form,'input');
            i=0; lim=tosync.length;
