        var passageid=((passage.codexbaseid)||(passage.id));
        var formid=((gloss)?
                    ((response)?
                     ("METABOOKRESPONDGLOSS_"+gloss._id):
                     ("METABOOKEDITGLOSS_"+gloss._id)):
                    ("METABOOKADDGLOSS_"+passageid));
        var form=$ID(formid);
